/* 修复input 背景不协调 和光标变色 */
/* Detail see https://github.com/PanJiaChen/vue-element-admin/pull/927 */

$bg:#283443;
$light_gray:#fff;
$cursor: #fff;
$input_color:#B1B1B1;

@supports (-webkit-mask: none) and (not (cater-color: $cursor)) {
  .login-container .el-input input {
    color: $cursor;
  }
}

/* reset element-ui css */
.login-container {
  .el-input {
    display: inline-block;
    // height: 47px;
    width: 85%;

    input {
      background: #F7F4FB;
      border: 0px;
      -webkit-appearance: none;
      border-radius: 34px;
      padding-left:30px;
      color: $input_color;
      height: 68px;
      caret-color: $cursor;
      font-size:20px;

      &:-webkit-autofill {
        box-shadow: 0 0 0px 1000px $bg inset !important;
        -webkit-text-fill-color: $cursor !important;
      }
    }
  }

  .el-form-item {
    width: 480px;
    border: none;
    // background: rgba(0, 0, 0, 0.1);
    border-radius: 5px;
    color: #454545;
  }
  .el-form-item__error {
    left:30px;
  }
  .loginBtn {
    border:none;
    width:220px;
    height:60px;
    font-size:24px;
    margin-top:20px;
    background:#2CA17C;
    color:#fff;
    border-radius:30px;
    box-shadow:0px 17px 21px 0px rgba(44,165,127,0.2);
  }
}
