$bg:#fff;
$dark_gray:#889aa4;
$light_gray:#eee;
$dark_green:#27745B;

.login-container {
  min-height: 100%;
  width: 100%;
  min-width: 1440px;
  background: $bg url("../../assets/login/bg.png") no-repeat right;
  overflow: hidden;
  background-size:contain;

  .login-form {
    width: 880px;
    padding: 3% 35px 30px 119px;
    overflow: hidden;
  }
  .tips {
    margin-top:50px;
    font-size: 20px;
    color: #2CA47E;
    margin-bottom: 10px;
  }

  .svg-container {
    padding: 6px 5px 6px 15px;
    color: $dark_gray;
    vertical-align: middle;
    width: 30px;
    display: inline-block;
  }

  .title-container {
    position: relative;

    .title {
      font-size: 60px;
      color: $dark_green;
      // margin: 0px auto 40px auto;
      text-align: left;
      font-weight: bold;
    }
    .tip_txt {
      font-size:30px;
      color:#0E121A;
    }
    .little_font {
      margin-bottom:50px;
      font-size: 26px;
    }
  }

  .show-pwd {
    position: absolute;
    right: 10px;
    top: 7px;
    font-size: 16px;
    color: $dark_gray;
    cursor: pointer;
    user-select: none;
  }
}
